import { Link } from "react-router-dom"
import { isAuthenticated } from "../services/Auth"
import './NavBar.css'
import { MenuItem, MenuList } from "@mui/material"
export default function NavBar(props){
    const [open, setOpen] = React.useState(false);
    const anchorRef = React.useRef(null);

    const handleToggle = () => {
      setOpen((prevOpen) => !prevOpen);
    };



    return ( <nav  className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container-fluid">
                <a className="navbar-brand" href="#">User Authenticator</a>
                <button className="navbar-toggler"  aria-controls={open ? 'composition-menu' : undefined}
          aria-expanded={open ? 'true' : undefined}
          aria-haspopup="true"
          onClick={handleToggle}
        type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup"   aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                    <MenuList>

                    <ul className="navbar-nav"  >
                        {!isAuthenticated()?(<MenuItem><li className="nav-item"><Link className="nav-link" to="/">Register</Link></li></MenuItem>):null}
                        {!isAuthenticated()?(<MenuItem><li><Link className="nav-link" to="/login" >Login</Link></li></MenuItem>):null}
                        {isAuthenticated()?(<MenuItem><li className="nav-item"><Link className="nav-link" to="/dashboard" >Dashboard</Link></li></MenuItem>):null}
                        {isAuthenticated()?(<MenuItem><li><a className="nav-link"  onClick={props.logoutUser} style={{cursor:"pointer"}} >Logout</a></li></MenuItem>):null}
                    </ul>
                    </MenuList>
                </div>
                </div>
            </nav>
            )
}
